import React, { Component, useState, useEffect } from "react";
import FullPageIntroWithFixedTransparentNavbar from "./mainPage";
import AdminLogin from "./components/AdminLogin";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import { auth } from './index'
import AdminRoute from "./components/AdminRoute";







function App() {
const [firebaseinItialized, setFirebaseInitialize] = useState(false)

  useEffect(() =>{
    // function isinit() {
    //  return new Promise(resolve => {
    //    auth.onAuthStateChanged(resolve =>{
    //      if(resolve)
    //      {
    //        console.log('yes')
    //      }
    //      return resolve
    //    })
    //  })
    //  }
    //  isinit().then(val => {
    //    setFirebaseInitialize(val)
    //  })
    auth.onAuthStateChanged(isLoggedIn => {
      setFirebaseInitialize(isLoggedIn)
    })
   },[])

    return !firebaseinItialized ? (
      <div className="App">
        <Router>
          <Switch>
          <Route path = '/AdminRoute' component={AdminRoute} />
          <Route exact path="/(Admin)" component={AdminLogin} />
          <Route component={FullPageIntroWithFixedTransparentNavbar} />
          </Switch>
        </Router>
      </div>
    ) : (
       <div><h3>waiting</h3></div>
    )
}
export default App;
