import React, { Component } from "react";
import {
  MDBContainer,
  MDBBtn,
  MDBModal,
  MDBModalBody,
  MDBModalHeader,
  MDBModalFooter,
} from "mdbreact";
import "../css/UploadEvent.css";
import "../css/Admin.css";
import InputDropzoneComponent from "./Components/InputDropzoneComponent";
class UploadFeedBack extends Component {
  state = {
    modal16: false,
    pictureName: "",
    pictureUrl: "",
    pictureFiles: [],
    isActive1: false,
    isActive2: false,
  };

  onRemoveFile = (file) => {
    var tempPictures = this.state.pictureFiles.filter((item, index) => {
      return item != file;
    });
    this.setState({
      pictureFiles: tempPictures,
    });
  };
  onAddFile = (file) => {
    this.setState({
      pictureFiles: [...this.state.pictureFiles, file],
    });
  };

  clickHandler = async (event) => {
    event.preventDefault();
    if(!event.target.classList.contains('was-validated'))
    event.target.className += " was-validated"
    if (this.state.pictureFiles.length) {
      let modalNumber = "modal" + 16;
      this.props.addPictureHandle(this.state.pictureFiles);
      this.setState({
        ...this.state,
        [modalNumber]: !this.state[modalNumber],
      });
    }
  };

  toggle = (nr) => () => {
    let modalNumber = "modal" + nr;
    this.setState({
      [modalNumber]: !this.state[modalNumber],
    });
  };

  render() {
    const { disabled } = this.props;
    return (
      <MDBContainer>
        <MDBBtn
          style={disabled ? { pointerEvents: "none", opacity: "0.4" } : {}}
          onClick={this.toggle(16)}
          rounded
          outline
          color="success"
        >
          הוסף תמונה לגלרייה
        </MDBBtn>
        <MDBModal
          position="left-top"
          isOpen={this.state.modal16}
          toggle={this.toggle(16)}
        >
          <MDBModalHeader toggle={this.toggle(16)} dir="rtl">
            הוסף תמונה
          </MDBModalHeader>
          <form className="needs-validation" noValidate onSubmit={this.clickHandler}>
            <MDBModalBody style={{ maxHeight: "75vh" }}>
              <InputDropzoneComponent
                required
                onAddFile={this.onAddFile}
                onRemoveFile={this.onRemoveFile}
              />

               <InputDropzoneComponent/>
            </MDBModalBody>
            <MDBModalFooter>
              <MDBBtn color="secondary" onClick={this.toggle(16)}>
                ביטול
              </MDBBtn>
              <MDBBtn type="submit" color="primary">
                הוסף תמונות
              </MDBBtn>
            </MDBModalFooter>
          </form>
        </MDBModal>
      </MDBContainer>
    );
  }
}

export default UploadFeedBack;
