import React, { Component } from "react";
import { MDBCol, MDBContainer, MDBRow,MDBCarousel, MDBCarouselInner, MDBCarouselItem, MDBTestimonial, MDBAvatar, MDBIcon } from "mdbreact";
import  {db} from "./index";




class FooterPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      finish: false,
      view : [],
    };

    db.collection("FeedBack").get().then((querySnapshot) => {
      var MDBCarouselItems = [];
      var colums  = [];
      var fourColums = []; 
      querySnapshot.forEach((item) => {
      console.log(item.data()); 
      colums.push(<MDBCol style={{display : "inline-block"}} md="3">
                            <MDBTestimonial>
                             <MDBAvatar  style={{width : "80px"}} className="mx-auto">
                               <img
                                 src="https://mdbootstrap.com/img/Photos/Avatars/img%20(26).jpg"
                                 alt=""
                                 className="rounded-circle img-fluid"
                               />
                           </MDBAvatar>
                             <h6 className="font-weight-bold mt-4"> ${item.data().name}</h6>
                            <p className="font-weight-normal">
                              <MDBIcon icon="quote-left" className="pr-2" />
                              ${item.data().feedBackTxt}
                             </p>
                          </MDBTestimonial>
                          </MDBCol>)
               })
               console.log(colums);
               colums.forEach((item,index) => {
                if(index !== 3){
                  fourColums.push(item);
                }
                else if(fourColums.length){
                  console.log(index);
                  console.log(fourColums);
                  var Id = index / 4;
                  if(Id == 0){
                    Id = 1;
                  }
                  MDBCarouselItems.push (
                          <MDBCarouselItem itemId = {Id} > 
                           {fourColums.pop()};
                           {fourColums.pop()};
                           {fourColums.pop()};
                           {fourColums.pop()};
                          </MDBCarouselItem>)
                }        
              });
              console.log(MDBCarouselItems);
              console.log('state will update');

              this.setState((state, props) => ({
                finish: true,
                view : <MDBContainer>
                <section className="text-center my-5">
                  <MDBCarousel
                    activeItem={1}
                    length={3}
                    slide={true}
                    showControls={true}
                    multiItem
                    testimonial
                  >
                    <MDBCarouselInner >
                      <MDBRow>
                     {this.builedView()}
                     </MDBRow>
                   </MDBCarouselInner>
                  </MDBCarousel>
                </section>
              </MDBContainer>,
              }));

              // return ( <MDBContainer>
              //   <section className="text-center my-5">
              //     <MDBCarousel
              //       activeItem={1}
              //       length={3}
              //       slide={true}
              //       showControls={true}
              //       multiItem
              //       testimonial
              //     >
              //       <MDBCarouselInner >
              //         <MDBRow>
              //        {this.builedView()}
              //        </MDBRow>
              //      </MDBCarouselInner>
              //     </MDBCarousel>
              //   </section>
              // </MDBContainer>)
              // });
            });
  }

// builedView = () =>{
//   console.log("start build view");

//           }


render(){
  if(this.state.finish){
  return(
    <Function data = {this.state.view}></Function>
  );
  }
  else{
    return(null);
  }
}

}

export default FooterPage;
