import React from "react";
import {
  MDBIcon,
  MDBContainer,
  MDBTable,
  MDBTableBody,
  MDBTableHead,
  MDBBtn,
} from "mdbreact";

const AddFeedBack = (state) => {
  const { feedBack } = state;
};

const FeedBackTable = (state) => {
  const { feedBack } = state;
  return (
    <MDBContainer style={{ fontSize: "3rem" }}>
      <MDBTable>
        <MDBTableHead>
          <tr className="text-right">
            <th>מס׳ פידבק</th>
            <th>שם כותב הפידבק</th>
            <th> תוכן הפידבק</th>
            <th>
              <MDBBtn outline color="warning" onClick={AddFeedBack}>
                הוסף
              </MDBBtn>
            </th>
          </tr>
        </MDBTableHead>
        <MDBTableBody>
          {feedBack.map((item, index) => {
            return (
              <tr key={index} className="text-right">
                <td>{index}</td>
                <td>{item.data().name}</td>
                <td>{item.data().feedBackTxt}</td>
                <td>
                  <MDBBtn outline color="danger">
                    מחק
                  </MDBBtn>
                </td>
              </tr>
            );
          })}
        </MDBTableBody>
      </MDBTable>
    </MDBContainer>
  );
};

export default FeedBackTable;
