import React, { Component } from "react";
import {
  MDBCard,
  MDBCardBody,
  MDBContainer,
  MDBMask,
  MDBIcon,
  MDBView,
} from "mdbreact";
import { storage } from "./index";
import FooterPage from "./footer.js";
import {
  VerticalTimeline,
  VerticalTimelineElement,
} from "react-vertical-timeline-component";
import "react-vertical-timeline-component/style.min.css";
import image_1 from "./Images/TimeLine/image_1.jpg";
import image_2 from "./Images/TimeLine/image_2.jpg";
import image_3 from "./Images/TimeLine/image_3.jpg";
import image_4 from "./Images/TimeLine/image_4.jpg";
import TimeLineIcon from "./Images/TimeLine/TimeLineIcon.png";
import BackGroundVideo from "./Video/BackGroundVideo.mp4";
import Loader from "./components/loader.js";

class main extends Component {
    state = {
      videoSrc: "",
    };

  componentDidMount() {
    //   const storageRef = storage.ref();
    //   const imagesRef = storageRef.child("BackGround Video");
    //   imagesRef.listAll().then((res) => {
    //     res.items.forEach((element, index) => {
    //       element.getDownloadURL().then((url) => {
    //         this.setState({
    //           videoSrc: url,
    //         });
    //         document.getElementById("videoTag").load();
    //       });
    //     });
    //   });
  }

  render() {
    if (this.state.Loader) {
      return <Loader />;
    } else {
      return (
        <div style={{ background: "#607d8b42" }}>
          <MDBView>
            <video width="100%" id="videoTag" autoPlay loop muted>
              <source src={BackGroundVideo} type="video/mp4" />
            </video>
          </MDBView>
          <main>
            <MDBCard
              color="mdb-color lighten-2"
              text="white"
              className="text-center"
            >
              <MDBCardBody>
                <h2 className="h2-responsive font-weight-normal text-center my-5">
                  מסע חברתי הוא ארגון המחבר בין כמה עולמות
                </h2>
              </MDBCardBody>
            </MDBCard>
            <MDBContainer className="text-center my-5">
              <VerticalTimeline>
                <VerticalTimelineElement
                  className="vertical-timeline-element--work"
                  contentStyle={{
                    webkiBoxShadow: "7px 7px 47px -13px rgba(0,0,0,0.96)",
                    MozBoxShadow: "7px 7px 47px -13px rgba(0,0,0,0.96)",
                    boxShadow: "7px 7px 47px -13px rgba(0,0,0,0.96)",
                    height: 250,
                    backgroundImage: `url(${image_1})`,
                    backgroundRepeat: "no-repeat",
                    backgroundOrigin: "content-box",
                    backgroundPosition: "center",
                    backgroundSize: 500,
                    textAlign: "center",
                    fontWeight: "bold",
                    color: "white",
                    letterSpacing: "2px",
                    textShadow: "0.5px 0.5px black",
                  }}
                  contentArrowStyle={{
                    borderRight: "1px solid  rgb(33, 150, 243)",
                  }}
                  iconStyle={{
                    backgroundImage: `url(${TimeLineIcon})`,
                    backgroundRepeat: "no-repeat",
                    backgroundOrigin: "content-box",
                    backgroundPosition: "center",
                    backgroundSize: 99,
                  }}
                  // icon = {<TimeLineIcon/>}
                >
                  <h3
                    style={{ fontWeight: "500" }}
                    className="vertical-timeline-element-title"
                  >
                    20.11.2020
                  </h3>
                  <h4
                    style={{ fontWeight: "300" }}
                    className="vertical-timeline-element-subtitle"
                  >
                    חרדים וערבים יחדיו
                  </h4>
                  <p>מסע אל לב ליבה של התרבות החרדית והערבית יחדיו</p>
                </VerticalTimelineElement>
                <VerticalTimelineElement
                  className="vertical-timeline-element--work"
                  contentStyle={{
                    webkiBoxShadow: "7px 7px 47px -13px rgba(0,0,0,0.96)",
                    MozBoxShadow: "7px 7px 47px -13px rgba(0,0,0,0.96)",
                    boxShadow: "7px 7px 47px -13px rgba(0,0,0,0.96)",
                    height: 250,
                    backgroundImage: `url(${image_2})`,
                    backgroundRepeat: "no-repeat",
                    backgroundOrigin: "content-box",
                    backgroundPosition: "center",
                    backgroundSize: 500,
                    textAlign: "center",
                    fontWeight: "bold",
                    color: "white",
                    letterSpacing: "2px",
                    textShadow: "0.5px 0.5px black",
                  }}
                  contentArrowStyle={{
                    borderRight: "1px solid  rgb(33, 150, 243)",
                  }}
                  iconStyle={{
                    backgroundImage: `url(${TimeLineIcon})`,
                    backgroundRepeat: "no-repeat",
                    backgroundOrigin: "content-box",
                    backgroundPosition: "center",
                    backgroundSize: 40,
                  }}
                >
                  <h3
                    style={{ fontWeight: "500" }}
                    className="vertical-timeline-element-title"
                  >
                    20.11.2020
                  </h3>
                  <h4
                    style={{ fontWeight: "300" }}
                    className="vertical-timeline-element-subtitle"
                  >
                    חרדים וערבים יחדיו
                  </h4>
                  <p>מסע אל לב ליבה של התרבות החרדית והערבית יחדיו</p>
                </VerticalTimelineElement>
                <VerticalTimelineElement
                  className="vertical-timeline-element--work"
                  contentStyle={{
                    webkiBoxShadow: "7px 7px 47px -13px rgba(0,0,0,0.96)",
                    MozBoxShadow: "7px 7px 47px -13px rgba(0,0,0,0.96)",
                    boxShadow: "7px 7px 47px -13px rgba(0,0,0,0.96)",
                    height: 250,
                    backgroundImage: `url(${image_3})`,
                    backgroundRepeat: "no-repeat",
                    backgroundOrigin: "content-box",
                    backgroundPosition: "center",
                    backgroundSize: 500,
                    textAlign: "center",
                    fontWeight: "bold",
                    color: "white",
                    letterSpacing: "2px",
                    textShadow: "0.5px 0.5px black",
                  }}
                  contentArrowStyle={{
                    borderRight: "1px solid  rgb(33, 150, 243)",
                  }}
                  iconStyle={{
                    backgroundImage: `url(${TimeLineIcon})`,
                    backgroundRepeat: "no-repeat",
                    backgroundOrigin: "content-box",
                    backgroundPosition: "center",
                    backgroundSize: 40,
                  }}
                >
                  <h3
                    style={{ fontWeight: "500" }}
                    className="vertical-timeline-element-title"
                  >
                    20.11.2020
                  </h3>
                  <h4
                    style={{ fontWeight: "300" }}
                    className="vertical-timeline-element-subtitle"
                  >
                    חרדים וערבים יחדיו
                  </h4>
                  <p>מסע אל לב ליבה של התרבות החרדית והערבית יחדיו</p>
                </VerticalTimelineElement>
                <VerticalTimelineElement
                  className="vertical-timeline-element--work"
                  contentStyle={{
                    webkiBoxShadow: "7px 7px 47px -13px rgba(0,0,0,0.96)",
                    MozBoxShadow: "7px 7px 47px -13px rgba(0,0,0,0.96)",
