import React from "react";
import { MDBCol, MDBContainer, MDBRow,MDBCarousel, MDBCarouselInner, MDBCarouselItem, MDBTestimonial, MDBAvatar, MDBIcon } from "mdbreact";
import  {db} from "./index";


 const FooterPage  = () => {
  var MDBCarouselItems = [];
  var colums  = [];
  var fourColums = []; 
  db.collection("FeedBack").get().then(function(querySnapshot) {
    querySnapshot.forEach((item)=> {
    console.log(item.data());
    colums.push(<MDBCol style={{display : "inline-block"}} md="3">
                          <MDBTestimonial>
                           <MDBAvatar  style={{width : "80px"}} className="mx-auto">
                             <img
                               src="https://mdbootstrap.com/img/Photos/Avatars/img%20(26).jpg"
                               alt=""
                               className="rounded-circle img-fluid"
                             />
                         </MDBAvatar>
                           <h6 className="font-weight-bold mt-4"> ${item.data().name}</h6>
                          <p className="font-weight-normal">
                            <MDBIcon icon="quote-left" className="pr-2" />
                            ${item.data().feedBackTxt}
                           </p>
                        </MDBTestimonial>
                        </MDBCol>)
             })
             console.log(colums);
             colums.forEach((item,index)=>{
              //console.log("gal");
              if(index % 3 !== 0){
                fourColums.push(item);
              }
              else if(fourColums){
                console.log(iNDEX)
                console.log(fourColums);
                var Id = index / 4;
                if(Id == 0){
                  Id = 1;
                }
                MDBCarouselItems.push(
                        <MDBCarouselItem itemId = {Id} > 
                         {fourColums.pop()};
                         {fourColums.pop()};
                         {fourColums.pop()};
                         {fourColums.pop()};
                        </MDBCarouselItem>)
              }
            });
            console.log(MDBCarouselItems);
            });




         return (<MDBContainer>
           <section className="text-center my-5">
             <MDBCarousel
               activeItem={1}
               length={3}
               slide={true}
               showControls={true}
               multiItem
               testimonial
             >
               <MDBCarouselInner >
                 <MDBRow>
                {MDBCarouselItems}
                </MDBRow>
              </MDBCarouselInner>
             </MDBCarousel>
           </section>
         </MDBContainer>);
      } 

export default FooterPage;
