class footer extends Component {

  getFeedBack(){
    db.collection("FeedBack").get()
    .then((querySnapshot) => {
      var MDBCarouselItems = [];
      var colums  = [];
      var fourColums = []; 
      MDBCarouselItems =  querySnapshot.map((item,index) => {
        return <MDBCol style={{display : "inline-block"}} md="3">
                            <MDBTestimonial>
                             <MDBAvatar  style={{width : "80px"}} className="mx-auto">
