import React, { Component } from 'react';
import { MDBContainer, MDBBtn, MDBModal, MDBModalBody, MDBModalHeader, MDBModalFooter, MDBCol, MDBRow, MDBInput } from
  'mdbreact';
  import DatePicker from './DatePicker'
  import FileInput from './FileInput'

  


class UploadEvent extends Component {
  state = {
    modal16: false,
    eventName: '',
    filePath: '',
    date: '',
    audiance: '',
    description: ''

  }

  toggle = nr => () => {
    let modalNumber = 'modal' + nr
    this.setState({
      [modalNumber]: !this.state[modalNumber]
    });
  }

  render() {
    return (
      <MDBContainer>
        <MDBBtn onClick={this.toggle(16)}>MDBModal</MDBBtn>
        <MDBModal position="left-top" isOpen={this.state.modal16} toggle={this.toggle(16)}>
          <MDBModalHeader toggle={this.toggle(16)} dir="rtl" >הוסף מסע</MDBModalHeader>
          <MDBModalBody >
            <div className="form-group">
              <MDBInput type="text" labelClass="col text-right" className="col text-right" label="שם האירוע" getValue={(e) => this.setState( ...this.state,{eventName: e.target})}/>
              <FileInput getValue={(e) => this.setState(...this.state, {filePath: e.target})}/>
              <DatePicker/>
              <MDBInput type="text" labelClass="col text-right" label="קהל יעד" className="col text-right" />
              <MDBInput type="textarea" labelClass="col text-right" label="תיאור קצר" className="col text-right" />
              <MDBInput type="textarea" labelClass="col text-right" label="תיאור מלא" className="col text-right" />
            </div>
          </MDBModalBody>
          <MDBModalFooter>
            <MDBBtn color="secondary" onClick={this.toggle(16)}>Close</MDBBtn>
            <MDBBtn color="primary">Save changes</MDBBtn>
          </MDBModalFooter>
        </MDBModal>
      </MDBContainer>
    );
  }
}

export default UploadEvent;
