import React from "react";
import {
  MDBDataTable,
  MDBContainer,
  MDBBtn,
  MDBModalFooter,
  MDBModalHeader,
  MDBModal,
  MDBRow,
} from "mdbreact";
import { useEffect, useState, useRef } from "react";
import { db } from "../../index";


const TimeLineTable = () => {
    const [open, setopen] = useState(false);
    const eventIdRef = useRef(null);
    const [timeLineEvents, setEvents] = useState([]);
    const [modalIsOpen, setmodalIsOpen] = useState({ index: 1 });
    const objArray = [];
    const data = {
      columns: [
        {
          label: "תאריך אירוע",
          field: "date",
          sort: "asc",
          width: 80,
        },
        {
          label: "כותרת",
          field: "title",
          sort: "asc",
          width: 100,
        },
        {
          label: "תוכן",
          field: "content",
          sort: "asc",
          width: 270,
        },
        {
          label: "ערוך אירוע",
          field: "edit",
          width: 100,
        },
      ],

      rows: timeLineEvents,
    };

  const areYouSure = (feedBack) => {
    eventIdRef.current = feedBack.target.id;
    setopen(true);
  };

  const deleteHandler = () => {
    // db.collection("FeedBack")
    //   .doc(`${feedBackIdRef.current}`)
    //   .delete()
    //   .then(function () {
    //     console.log("Document successfully deleted!");
    //     setopen(false);
    //   })
    //   .catch(function (error) {
    //     console.error("Error removing document: ", error);
    //   });
  };

  useEffect(() => {
    db.collection("TimeLineEvents")
      .get()
      .then((querySnapshot) => {
        querySnapshot.forEach((item) => {
          objArray.push({
            date: item.data().date,
            title: item.data().title,
            content: item.data().content,
            edit: (
              <MDBBtn
                size="sm"
                outline
                color="primary"
                id={`${item.id}`}
                onClick={areYouSure}
              >
                ערוך אירוע
              </MDBBtn>
            ),
          });
        });
        setEvents(objArray);
      });
  }, [open, modalIsOpen]);

  return (
    <MDBContainer style={{ backgroundColor: "white" }}>
      <MDBDataTable
        entriesLabel="מספר הערכים בטבלה"
        paginationLabel={["הקודם", "הבא"]}
        infoLabel={["מראה", "עד", "מתוך", "ערכים"]}
        className="text-right justify-content-center"
        searchLabel="חיפוש"
        btn
        scrollY
        maxHeight="65vh"
        striped
        bordered
        small
        data={data}
      />
      <MDBModal
        isOpen={open}
        toggle={() => setopen(false)}
        backdrop={true}
        size="sm"
      >
        <MDBModalHeader
          style={{ backgroundColor: "#ff4444" }}
          className="text-white justify-content-center"
        >
          ערוך 
        </MDBModalHeader>
        <MDBModalFooter className="justify-content-center">
          <MDBBtn color="danger" onClick={() => setopen(false)}>
            ביטול
          </MDBBtn>
          <MDBBtn outline color="danger" onClick={deleteHandler}>
            מחק
          </MDBBtn>
        </MDBModalFooter>
      </MDBModal>
    </MDBContainer>
  );
};

export default TimeLineTable;
