import React, { Component } from "react";
import { MDBContainer, MDBIcon, MDBView } from "mdbreact";
import { storage } from "./index";
import FooterPage from "./footer.js";
import {
  VerticalTimeline,
  VerticalTimelineElement,
} from "react-vertical-timeline-component";
import "react-vertical-timeline-component/style.min.css";
import Desert from "./2.jpg";
import desert from "./desert.jpg";

class main extends Component {
  state = {
    videoSrc: "",
  };

  componentDidMount() {
    const storageRef = storage.ref();
    const imagesRef = storageRef.child("BackGround Video");
    imagesRef.listAll().then((res) => {
      res.items.forEach((element, index) => {
        element.getDownloadURL().then((url) => {
          this.setState({
            videoSrc: url,
          });
          document.getElementById("videoTag").load();
        });
      });
    });
  }

  render() {
    return (
      <div>
        <MDBView>
          <video id="videoTag" autoPlay playsInline autoPlay muted loop muted>
            <source src={this.state.videoSrc} type="video/mp4" />
          </video>
        </MDBView>
        <main>
          <MDBContainer className="text-center my-5">
            <VerticalTimeline>
              <VerticalTimelineElement
                className="vertical-timeline-element--work"
                contentStyle={{
                  height: 300,
                  backgroundImage: `url(${Desert})`,
                  backgroundRepeat: "no-repeat",
                  backgroundOrigin: "content-box",
                  backgroundPosition: "center",
                  backgroundSize: 500,
                }}
                contentArrowStyle={{}}
                // date="2011 - present"
                iconStyle={{
                  backgroundImage: `url(${desert})`,
                  backgroundRepeat: "no-repeat",
                  backgroundOrigin: "content-box",
                  backgroundPosition: "center",
                  backgroundSize: 100,
                }}
              >
                <h3 className="vertical-timeline-element-title">
                  Creative Director
                </h3>
                <h4 className="vertical-timeline-element-subtitle">
                  Miami, FL
                </h4>
                <p>
                  Creative Direction, User Experience, Visual Design, Project
                  Management, Team Leading
                </p>
              </VerticalTimelineElement>
              <VerticalTimelineElement
                className="vertical-timeline-element--work"
                contentStyle={{
                  height: 300,
                  backgroundImage: `url(${Desert})`,
                  backgroundRepeat: "no-repeat",
                  backgroundOrigin: "content-box",
                  backgroundPosition: "center",
                  backgroundSize: 500,
                }}
                contentArrowStyle={{}}
                // date="2011 - present"
                iconStyle={{
                  backgroundImage: `url(${desert})`,
                  backgroundRepeat: "no-repeat",
                  backgroundOrigin: "content-box",
                  backgroundPosition: "center",
                  backgroundSize: 100,
                }}
              >
                <h3 className="vertical-timeline-element-title">
                  Art Director
                </h3>
                <h4 className="vertical-timeline-element-subtitle">
                  San Francisco, CA
                </h4>
                <p>
                  Creative Direction, User Experience, Visual Design, SEO,
                  Online Marketing
                </p>
              </VerticalTimelineElement>
              <VerticalTimelineElement
                className="vertical-timeline-element--work"
                contentStyle={{
                  height: 300,
                  backgroundImage: `url(${Desert})`,
                  backgroundRepeat: "no-repeat",
                  backgroundOrigin: "content-box",
                  backgroundPosition: "center",
                  backgroundSize: 500,
                }}
                contentArrowStyle={{}}
                // date="2011 - present"
                iconStyle={{
                  backgroundImage: `url(${desert})`,
                  backgroundRepeat: "no-repeat",
                  backgroundOrigin: "content-box",
                  backgroundPosition: "center",
                  backgroundSize: 100,
                }}
              >
                <h3 className="vertical-timeline-element-title">
                  Web Designer
                </h3>
                <h4 className="vertical-timeline-element-subtitle">
                  Los Angeles, CA
                </h4>
                <p>User Experience, Visual Design</p>
              </VerticalTimelineElement>
              <VerticalTimelineElement
                className="vertical-timeline-element--work"
                contentStyle={{
                  height: 300,
                  backgroundImage: `url(${Desert})`,
                  backgroundRepeat: "no-repeat",
                  backgroundOrigin: "content-box",
                  backgroundPosition: "center",
                  backgroundSize: 500,
                }}
                contentArrowStyle={{}}
                // date="2011 - present"
                iconStyle={{
                  backgroundImage: `url(${desert})`,
                  backgroundRepeat: "no-repeat",
                  backgroundOrigin: "content-box",
                  backgroundPosition: "center",
                  backgroundSize: 100,
                }}
              >
                <h3 className="vertical-timeline-element-title">
                  Web Designer
                </h3>
                <h4 className="vertical-timeline-element-subtitle">
                  San Francisco, CA
                </h4>
                <p>User Experience, Visual Design</p>
              </VerticalTimelineElement>
              <VerticalTimelineElement
                className="vertical-timeline-element--education"
                contentStyle={{
                  height: 300,
                  backgroundImage: `url(${Desert})`,
                  backgroundRepeat: "no-repeat",
                  backgroundOrigin: "content-box",
                  backgroundPosition: "center",
                  backgroundSize: 500,
                }}
                contentArrowStyle={{}}
                // date="2011 - present"
                iconStyle={{
                  backgroundImage: `url(${desert})`,
                  backgroundRepeat: "no-repeat",
                  backgroundOrigin: "content-box",
                  backgroundPosition: "center",
                  backgroundSize: 100,
                }}
              >
                <h3 className="vertical-timeline-element-title">
                  Content Marketing for Web, Mobile and Social Media
                </h3>
                <h4 className="vertical-timeline-element-subtitle">
                  Online Course
                </h4>
                <p>Strategy, Social Media</p>
              </VerticalTimelineElement>
              <VerticalTimelineElement
                className="vertical-timeline-element--education"
                contentStyle={{
                  height: 300,
                  backgroundImage: `url(${Desert})`,
                  backgroundRepeat: "no-repeat",
                  backgroundOrigin: "content-box",
                  backgroundPosition: "center",
                  backgroundSize: 500,
                }}
                contentArrowStyle={{}}
                // date="2011 - present"
                iconStyle={{
                  backgroundImage: `url(${desert})`,
                  backgroundRepeat: "no-repeat",
                  backgroundOrigin: "content-box",
                  backgroundPosition: "center",
                  backgroundSize: 100,
                }}
              >
                <h3 className="vertical-timeline-element-title">
                  Agile Development Scrum Master
                </h3>
                <h4 className="vertical-timeline-element-subtitle">
                  Certification
                </h4>
                <p>Creative Direction, User Experience, Visual Design</p>
              </VerticalTimelineElement>
              <VerticalTimelineElement
                className="vertical-timeline-element--education"
                contentStyle={{
                  height: 300,
                  backgroundImage: `url(${Desert})`,
                  backgroundRepeat: "no-repeat",
                  backgroundOrigin: "content-box",
                  backgroundPosition: "center",
                  backgroundSize: 500,
                }}
                contentArrowStyle={{}}
                // date="2011 - present"
                iconStyle={{
                  backgroundImage: `url(${desert})`,
                  backgroundRepeat: "no-repeat",
                  backgroundOrigin: "content-box",
                  backgroundPosition: "center",
                  backgroundSize: 100,
                }}
              >
                <h3 className="vertical-timeline-element-title">
                  Bachelor of Science in Interactive Digital Media Visual
                  Imaging
                </h3>
                <h4 className="vertical-timeline-element-subtitle">
                  Bachelor Degree
                </h4>
                <p>Creative Direction, Visual Design</p>
              </VerticalTimelineElement>
              <VerticalTimelineElement
                contentStyle={{
                  height: 300,
                  backgroundImage: `url(${Desert})`,
                  backgroundRepeat: "no-repeat",
                  backgroundOrigin: "content-box",
                  backgroundPosition: "center",
                  backgroundSize: 500,
                }}
                // contentArrowStyle={{}}
                date="2011 - present"
                // iconStyle={{
                //   backgroundImage: `url(${desert})`,
                //   backgroundRepeat: "no-repeat",
                //   backgroundOrigin: "content-box",
                //   backgroundPosition: "center",
                //   backgroundSize: 100,
                // }}
              />
            </VerticalTimeline>
          </MDBContainer>
        </main>
        <FooterPage />
      </div>
    );
  }
}

export default main;
