import React from 'react';
import { MDBDataTable, MDBContainer, MDBIcon, MDBBtn } from 'mdbreact';
import { useEffect } from 'react'
import { storage, db } from '../../index'

const EventsTable = () => {
    const data = {
        columns: [
            {
                label: 'שם',
                field: 'name',
                sort: 'asc',
                width: 150
            },
            {
                label: 'תאריך',
                field: 'date',
                sort: 'asc',
                width: 270
            },
            {
                label: 'קהל יעד',
                field: 'audience',
                sort: 'asc',
                width: 200
            },
            {
                label: 'מחק אירוע',
                field: 'delete',
                sort: 'asc',
                width: 100
            },
            {
                label: 'צפייה בנרשמים',
                field: 'participants',
                sort: 'asc',
                width: 150
            },
        ],
        rows: [

        ]
    };

    useEffect(() => {
        let date
        console.log('in useEffect')
        db.collection('events').get()
            .then(querySnapshot => {
                console.log(querySnapshot)
                querySnapshot.forEach(item => {
                    date = item.data().date
                    date = date.toString().split("-").reverse().join("-")
                    data.rows.push({
                        name: item.data().eventName,
                        date: date,
                        delete: <MDBBtn size="sm" color="purple"></MDBBtn>,
                        participants: <MDBIcon icon="users" />
                    })
                })
            })
    })

    return (
        <MDBContainer style={{ backgroundColor: "white" }}>
            <MDBDataTable
                btn="true"
                scrollY
                maxHeight="40vh"
                striped
                bordered
                small
                data={data}
            />
        </MDBContainer>
    );
}

export default EventsTable;
