import React, { useState, Fragment, useRef } from "react";
import {
  MDBCard,
  MDBCardBody,
  MDBCardImage,
  MDBCardTitle,
  MDBCardText,
  MDBRow,
  MDBCol,
  MDBContainer,
  MDBBtn,
  MDBModal,
  MDBCardGroup,
  MDBInput,
  MDBIcon
} from "mdbreact";
import Style from "../css/CardStyle.module.css";
import RegisterModal from "./RegisterModal";

const EventsCreator = (state) => {
  // const { eventsArr } = state;
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [register, setRegister] = useState(false)
  const [data, setdata] = useState({
    name: '',
    lastName: '',
    phoeNumber: '',
    email: '',
    adress: '',
    school: '',
    curriculum: '',
    why: '',
    expectaions: ''
  })
  const [errData, seterrData] = useState({
    errName: 'חובה להזין שם',
    errLastName: 'חובה להזין שם משפחה',
    errPhoeNumber: 'חובה להזין מספר טלפון',
    errEmail: 'חובה להזין כתובת אימייל',
    errExpectaions: 'חובה להזין ציפיות למסע'
  })
  // const [name, setName] = useState('')
  // const [lastname, setLastname] = useState('')
  // const [phoneNumber, setPhoneNumber] = useState('')
  // const [email, setEmail] = useState('')
  // const [adress, setName] = useState('')
  // const [adress, setAdress] = useState('')
  // const [school, setSchool] = useState('')
  // const [name, setName] = useState('')

  const imageRef = useRef(null);
  const titleRef = useRef(null);
  const desRef = useRef(null);
  const dateRef = useRef(null);


  const sendInfoHandler = (event) => {
    // event.preventDefault()
    // console.log(data)
  }


  function CreateCard(item) {
    titleRef.current = item.title;
    desRef.current = item.fullDesc;
    dateRef.current = item.date;
    imageRef.current = item.imageUrl;
    setModalIsOpen(true);
  }


  function SignModal(title) { //new
    setModalIsOpen(false)
    setRegister(true)
  }


  const cards = state.eventsArr.map((item, index) => {
    return (
      <MDBCol md="4" style={{ display: "inline-block" }}>
        <MDBCard style={{ height: "30rem" }} className="mb-4">
          <MDBCardImage
            top
            src={item.imageUrl}
            overlay="white-slight"
            hover
            waves
            alt="MDBCard image cap"
            style={{ height: "27vh" }}
          />
          <MDBCardBody>
            <MDBCardTitle className="text-right">{item.title}</MDBCardTitle>
            <hr />
            <MDBCardText className="text-right">{item.description}</MDBCardText>
          </MDBCardBody>
          <MDBBtn
            className={Style.btn}
            id={item.title}
            color="rgba(62, 69, 81, 0.3) rgba-stylish-light"
            onClick={() => CreateCard(item)}
          >
            קרא/י עוד
          </MDBBtn>
        </MDBCard>
      </MDBCol>
    );
  });

  return (
    <MDBContainer>
      <MDBCardGroup dir="rtl">
        {cards}
      </MDBCardGroup>
      <MDBModal
        isOpen={modalIsOpen}
        toggle={() => setModalIsOpen(false)}
        style={{
          overlay: {
            backgroundColor: "rgba(0,0,0,0.4)",
          },
        }}
      >
        <MDBRow>
          <MDBCol style={{ maxWidth: "60rem" }}>
            <MDBCard reverse>
              <MDBCardImage
                top
                cascade
                style={{ height: '30vh' }}
                src={imageRef.current}
              />
              <MDBCardBody cascade className="text-center">
                <MDBCardTitle> {titleRef.current} </MDBCardTitle>
                <h5>
                  <strong>{dateRef.current}</strong>
                </h5>
                <MDBCardText className="text-right">
                  {desRef.current}
                </MDBCardText>
                <Fragment>
                  <MDBBtn
                    color="rgba(62, 69, 81, 0.3) rgba-stylish-light"
                    onClick={() => SignModal(titleRef.current)}
                  >
                    הירשם
                  </MDBBtn>
                </Fragment>
              </MDBCardBody>
            </MDBCard>
          </MDBCol>
        </MDBRow>
      </MDBModal>
      {/* <MDBModal        // new
        size="lg"

        isOpen={register}
        toggle={() => setRegister(false)}
        style={{
          overlay: {
            backgroundColor: "rgba(0,0,0,0.4)",
          },

        }}
      >

        <h2 style={{ color: "black" }}>הרשמה למסע</h2>
        <h3 style={{ color: "black" }}>{titleRef.current}</h3>
        <h4 style={{ color: "black" }}> {dateRef.current}</h4>


        <MDBRow >
          <MDBCol md="11" >

            <form
               onSubmit={sendInfoHandler}
            >
              <div className="grey-text text-right  "  >
                <MDBInput
                  required
                  icon="user"
                  type="text"
                  labelClass="col text-right ml-0"
                  label="שם"
                  className="col text-right"
                  getValue={(e) => setdata({...data, name: e}), seterrData({...errData, errName: ''})}
                />


                <MDBInput
                  required
                  icon="fa fa-id-card"
                  type="text"
                  labelClass="col text-right ml-0"
                  label="שם משפחה"
                  className="col text-right"
                  getValue={(e) => setdata({...data, lastName: e}), seterrData({...errData, errLastName: ''})}
                />

                <MDBInput
                  required
                  icon="fa fa-phone"
                  type="number"
                  labelClass="col text-right ml-0"
                  label="פלאפון"
                  className="col text-right"
                  getValue={(e) => setdata({...data, phoneNumber: e}), seterrData({...errData, errPhoneNumber: ''})}
                />

                <MDBInput
                  required
                  icon="envelope"
                  type="email"
                  labelClass="col text-right ml-0"
                  label="מייל"
                  className="col text-right"
                  getValue={(e) => setdata({...data, email: e}), seterrData({...errData, errEmail: ''})}
                />

                <MDBInput
                  icon="fa fa-building"
                  type="text"
                  labelClass="col text-right ml-0"
                  label="כתובת"
                  className="col text-right"
                  getValue={(e) => setdata({...data, address: e})}
                />

                <MDBInput
                  icon="fa fa-graduation-cap"
                  type="text"
                  labelClass="col text-right ml-0"
                  label="מוסד לימודים"
                  className="col text-right"
                  getValue={(e) => setdata({...data, school: e})}
                />

                <MDBInput
                  icon="fa fa-graduation-cap"
                  type="text"
                  labelClass="col text-right ml-0"
                  label="מסלול לימודים"
                  className="col text-right"
                  getValue={(e) => setdata({...data, curriculum: e})}
                />

                <MDBInput
                  icon="tag"
                  type="textarea"
                  rows="2"
                  labelClass="col text-right ml-0"
                  label="מדוע אתה מעוניין להשתתף במסע"
                  className="col text-right"
                  getValue={(e) => setdata({...data, why: e})}
                />

                <MDBInput
                  required
                  icon="pencil-alt"
                  type="textarea"
                  rows="2"
                  labelClass="col text-right ml-0"
                  label="מה הציפיות שלך ממסע שכזה?"
                  className="col text-right"
                  getValue={(e) => setdata({...data, name: e}), seterrData({...errData, errExpectaions: ''})}
                />

              </div>


              <div dir="rtl" className="text-right">

                <div class="custom-control custom-checkbox custom-control-inline">
                  <input type="checkbox" class="custom-control-input" id="defaultInline1" />
                  <label class="custom-control-label" for="defaultInline1">מגיע עם רכב</label>
                </div>


                <div class="custom-control custom-checkbox custom-control-inline">
                  <input type="checkbox" class="custom-control-input" id="defaultInline2" />
                  <label class="custom-control-label" for="defaultInline2">האם תהיה מעוניין לקחת טרמפ משתתפים אחרים?</label>
                </div>


                <div class="custom-control custom-checkbox custom-control-inline">
                  <select className="browser-default custom-select">
                    <option>מספר מקומות פנויים</option>
                    <option value="1"> 1</option>
                    <option value="2"> 2</option>
                    <option value="3"> 3</option>
                    <option value="4"> 4</option>
                  </select>
                </div>



              </div>


              <div className="text-center">
                <MDBBtn outline type="submit" color="secondary">
                  שלח
            <MDBIcon far icon="paper-plane" className="ml-1" />
                </MDBBtn>
              </div>
            </form>
          </MDBCol>
        </MDBRow>


      </MDBModal> */}

    </MDBContainer>
  )

};

export default EventsCreator;
