import React, { Component } from "react";
import { MDBContainer, MDBMask, MDBView } from "mdbreact";
import { storage } from "./index";
import FooterPage from "./footer.js";
import BackGroundVideo from "./components/BackGroundVideo";
import { VerticalTimeline, VerticalTimelineElement }  from 'react-vertical-timeline-component';
import 'react-vertical-timeline-component/style.min.css';
class main extends Component {
  state = {
    videoSrc:
      "",
  };

  componentDidMount() {
    console.log("in comp fathdser");
    const storageRef = storage.ref();
    const imagesRef = storageRef.child("BackGround Video");
    imagesRef.listAll().then((res) => {
      res.items.forEach((element, index) => {
          element.getDownloadURL().then((url) => {
            console.log(url);
            this.setState({
              videoSrc: url,
            });
            document.getElementById("videoTag").load();
          });

      });
    });
  }

  render() {
    console.log("in fathder render");
    console.log(this.state.videoSrc);
    return (
      <div>
        <MDBView>
        <video id="videoTag" autoPlay="autoplay" loop="loop" muted>
        <source src = {this.state.videoSrc} type="video/mp4"/>
        </video>
        </MDBView>
        <main>
          <MDBContainer className="text-center my-5"></MDBContainer>
        </main>
        <FooterPage />
      </div>
    );
  }
}

export default main;
