import React from 'react';
import { MDBNavbar, MDBNavbarBrand, MDBNavbarNav, MDBNavbarToggler, MDBCollapse, MDBNavItem, MDBNavLink, MDBContainer, MDBMask, MDBView } from 'mdbreact';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import FooterPage from './components/footer.js'
import Donation from './components/Donation.js'
import Main from './components/main.js'
import AboutUs from './components/AboutUs.js'
import Events from './components/Events.js'
import ContactUs from './components/AboutUs.js'
import Gallery from './components/Gallery.js'

class FullPageIntroWithFixedTransparentNavbar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      collapse: false,
      isWideEnough: false,
    };
    this.onClick = this.onClick.bind(this);
  }

  onClick() {
    this.setState({
      collapse: !this.state.collapse,
    });
  }

  render() {
    return (
      <div>
        <header>
          <Router>
            <MDBNavbar color="bg-primary" fixed="top" dark expand="md" scrolling transparent>
              {!this.state.isWideEnough && <MDBNavbarToggler onClick={this.onClick} />}
              <MDBCollapse isOpen={this.state.collapse} navbar>
                <MDBNavbarNav center = "true">
                  <MDBNavItem >
                    <MDBNavLink to="./ContactUs"> צרו קשר</MDBNavLink>
                  </MDBNavItem>
                  <MDBNavItem >
                    <MDBNavLink to="./Donation">תרומות</MDBNavLink>
                  </MDBNavItem>
                  <MDBNavItem >
                    <MDBNavLink to="./Gallery">גלרייה</MDBNavLink>
                  </MDBNavItem>
                  <MDBNavItem>
                    <MDBNavLink to="./Evens">אירועים</MDBNavLink>
                  </MDBNavItem>
                  <MDBNavItem>
                    <MDBNavLink to="./components/AboutUs">אודות</MDBNavLink>
                  </MDBNavItem>
                </MDBNavbarNav>
              </MDBCollapse>
            </MDBNavbar>
            <Switch>
             <Route exact path='/' component={Main} />
             <Route exact path='./components/Donation' component={Donation} />
             <Route exact path='./components/AboutUs' component={AboutUs} />
             <Route exact path='./components/ContactUs' component={ContactUs} />
             <Route exact path='./components/Gallery' component={Gallery} />
             <Route exact path='./components/Evens' component={Events} />
            </Switch>
          </Router>
          <MDBView src="https://mdbootstrap.com/img/Photos/Others/img%20(40).jpg">
            <MDBMask overlay="purple-light" className="flex-center flex-column text-white text-center">
            </MDBMask>
            </MDBView>


        </header>
      </div>
    );
  }
}

export default FullPageIntroWithFixedTransparentNavbar;
