import React from "react";
import { MDBCol,MDBFooter, MDBContainer, MDBRow,MDBCarousel, MDBCarouselInner, MDBCarouselItem, MDBTestimonial, MDBAvatar, MDBIcon } from "mdbreact";
import  {db} from "./index";

 const feedBackView  = () => {
  var MDBCarouselItems ;
  var colums ;
  var fourColums; 
db.collection("FeedBack").get().then(function(querySnapshot) {
    querySnapshot.forEach(function(doc) {
        // doc.data() is never undefined for query doc snapshots
        console.log(doc.id, " => ", doc.data());
    });
});

