 }
class FooterPage extends React.Component {
constructor(props){
    super(props);
}



render(){
  return (
    <MDBContainer>
      <section className="text-center my-5">
        <MDBCarousel
          activeItem={1}
          length={3}
          slide={true}
          showControls={true}
          multiItem
          testimonial
        >
          <MDBCarouselInner >
            <MDBRow>
         {feedBackView()}
           </MDBRow>
          </MDBCarouselInner>
        </MDBCarousel>
      </section>
    </MDBContainer>
  );
}
}

export default FooterPage;
