import React from "react";
import { MDBCol,MDBFooter, MDBContainer, MDBRow,MDBCarousel, MDBCarouselInner, MDBCarouselItem, MDBTestimonial, MDBAvatar, MDBIcon } from "mdbreact";
import  {db} from "./index";

 const feedBackView  = () => {
  var MDBCarouselItems ;
  var colums  = [];
  var fourColums; 
  db.collection("FeedBack").get().then(function(querySnapshot) {
    querySnapshot.forEach((item)=> {
    colums.push(<MDBCol style={{display : "inline-block"}} md="3">
                          <MDBTestimonial>
                           <MDBAvatar  style={{width : "80px"}} className="mx-auto">
                             <img
                               src="https://mdbootstrap.com/img/Photos/Avatars/img%20(26).jpg"
                               alt=""
                               className="rounded-circle img-fluid"
                             />
                         </MDBAvatar>
                           <h6 className="font-weight-bold mt-4"> ${item.data().name}</h6>
                          <p className="font-weight-normal">
                            <MDBIcon icon="quote-left" className="pr-2" />
                            ${item.data().feedBackTxt}
                           </p>
                        </MDBTestimonial>
                        </MDBCol>)
             })
            });
              MDBCarouselItems = colums.map((item,index)=>{
              if(index % 4 != 0){
                fourColums.push(item);
              }
              else{
                var Id = index / 4;
                Id = Id == 0 ? Id + 1 : Id;
                return ( <MDBCarouselItem itemId = {Id} > 
                         {fourColums.pop()};
                         {fourColums.pop()};
                         {fourColums.pop()};
                         {fourColums.pop()};
                        </MDBCarouselItem>)
              }
        }).catch(function(error) {
            console.log("Error getting document:", error);
        });
        return MDBCarouselItems;
      } 



class FooterPage extends React.Component {
constructor(props){
    super(props);
}



render(){
  return (
    <MDBContainer>
      <section className="text-center my-5">
        <MDBCarousel
          activeItem={1}
          length={3}
          slide={true}
          showControls={true}
          multiItem
          testimonial
        >
          <MDBCarouselInner >
            <MDBRow>
         {feedBackView()}
           </MDBRow>
          </MDBCarouselInner>
        </MDBCarousel>
      </section>
    </MDBContainer>
  );
}
}

export default FooterPage;
