import React from "react";
import { MDBCol,MDBFooter, MDBContainer, MDBRow,MDBCarousel, MDBCarouselInner, MDBCarouselItem, MDBTestimonial, MDBAvatar, MDBIcon } from "mdbreact";
import  db from './index'

//  db.collection('FeedBack').doc('feedBackTxt').set({});


 const feedBackView  = () => {
  var MDBCarouselItems ;
  var colums ;
  var fourColums; 
  console.log('in feedBackView')
  var docRef = db.collection("FeedBack");
  docRef.get().then(function(doc) {
      if (doc.exists) {
        colums =  doc.data().map((item,index)=> {
         return (<MDBCol style={{display : "inline-block"}} md="3">
                    <MDBTestimonial>
                    <MDBAvatar  style={{width : "80px"}} className="mx-auto">
                      <img
                        src="https://mdbootstrap.com/img/Photos/Avatars/img%20(26).jpg"
                        alt=""
                        className="rounded-circle img-fluid"
                      />
                    </MDBAvatar>
                    <h6 className="font-weight-bold mt-4"> ${item.name}</h6>
                    <p className="font-weight-normal">
                      <MDBIcon icon="quote-left" className="pr-2" />
                     ${item.feedBackTxt}
                    </p>
                  </MDBTestimonial>
                 </MDBCol>)
        })
        MDBCarouselItems = colums.map((item,index)=>{
        if(index % 4 != 0){
          fourColums.push(item);
        }
        else{
          var Id = index / 4;
          Id = Id == 0 ? Id + 1 : Id;
          return ( <MDBCarouselItem itemId = {Id} > 
                   {fourColums}
                  </MDBCarouselItem>)
        }
        });
      } else {
          // doc.data() will be undefined in this case
          console.log("No such document!");
      }
  }).catch(function(error) {
      console.log("Error getting document:", error);
  });
} 
