import React, { Component } from "react";
import { MDBContainer, MDBIcon, MDBView } from "mdbreact";
import { storage } from "./index";
import FooterPage from "./footer.js";
import {
  VerticalTimeline,
  VerticalTimelineElement,
} from "react-vertical-timeline-component";
import "react-vertical-timeline-component/style.min.css";
import Desert from "./2.jpg";
import desert from "./desert.jpg";
import mountain from "./mountain.png"

class main extends Component {
  state = {
    videoSrc: "",
  };

  componentDidMount() {
    const storageRef = storage.ref();
    const imagesRef = storageRef.child("BackGround Video");
    imagesRef.listAll().then((res) => {
      res.items.forEach((element, index) => {
        element.getDownloadURL().then((url) => {
          this.setState({
            videoSrc: url,
          });
          document.getElementById("videoTag").load();
        });
      });
    });
  }

  render() {
    return (
      <div>
        <MDBView>
          <video id="videoTag" autoPlay playsInline autoPlay muted loop muted>
            <source src={this.state.videoSrc} type="video/mp4" />
          </video>
        </MDBView>
        <main>
          <MDBContainer className="text-center my-5">
            <VerticalTimeline>
              <VerticalTimelineElement
                className="vertical-timeline-element--work"
                contentStyle={{
                  height: 300,
                  backgroundImage: `url(${Desert})`,
                  backgroundRepeat: "no-repeat",
                  backgroundOrigin: "content-box",
                  backgroundPosition: "center",
                  backgroundSize: 500,
                  textAlign:'center',
                  fontWeight:'600',
                }}
                contentArrowStyle={{}}
                iconStyle={{
                  backgroundImage: `url(${mountain})`,
                  backgroundRepeat: "no-repeat",
                  backgroundOrigin: "content-box",
                  backgroundPosition: "center",
                  backgroundSize: 40,
                }}
              >
                <h3 style = {{color:'white',letterSpacing:'2px',textShadow: '0.5px 0.5px black'}}className="vertical-timeline-element-title">
                  20.11.2020
                </h3>
                <h4 style = {{color:'white',letterSpacing:'2px',textShadow: '0.5px 0.5px black'}} className="vertical-timeline-element-subtitle">
                 חרדים וערבים יחדיו
                </h4>
                <p style = {{color:'white',letterSpacing:'2px',textShadow: '0.5px 0.5px black'}} >
                  מסע אל לב ליבה של התרבות החרדית והערבית יחדיו
                </p>
              </VerticalTimelineElement>
              <VerticalTimelineElement
                className="vertical-timeline-element--work"
                contentStyle={{
                  height: 300,
                  backgroundImage: `url(${Desert})`,
                  backgroundRepeat: "no-repeat",
                  backgroundOrigin: "content-box",
                  backgroundPosition: "center",
                  backgroundSize: 500,
                }}
                contentArrowStyle={{}}
                iconStyle={{
                  backgroundImage: `url(${mountain})`,
                  backgroundRepeat: "no-repeat",
                  backgroundOrigin: "content-box",
                  backgroundPosition: "center",
                  backgroundSize: 40,
                }}
              >
                <h3 className="vertical-timeline-element-title">
                  Art Director
                </h3>
                <h4 className="vertical-timeline-element-subtitle">
                  San Francisco, CA
                </h4>
                <p>
                  Creative Direction, User Experience, Visual Design, SEO,
                  Online Marketing
                </p>
              </VerticalTimelineElement>
              <VerticalTimelineElement
                className="vertical-timeline-element--work"
                contentStyle={{
                  height: 300,
                  backgroundImage: `url(${Desert})`,
                  backgroundRepeat: "no-repeat",
                  backgroundOrigin: "content-box",
                  backgroundPosition: "center",
                  backgroundSize: 500,
                }}
                contentArrowStyle={{}}
                iconStyle={{
                  backgroundImage: `url(${mountain})`,
                  backgroundRepeat: "no-repeat",
                  backgroundOrigin: "content-box",
                  backgroundPosition: "center",
                  backgroundSize: 40,
                }}
              >
                <h3 className="vertical-timeline-element-title">
                  Web Designer
                </h3>
                <h4 className="vertical-timeline-element-subtitle">
                  Los Angeles, CA
                </h4>
                <p>User Experience, Visual Design</p>
              </VerticalTimelineElement>
              <VerticalTimelineElement
                className="vertical-timeline-element--work"
                contentStyle={{
                  height: 300,
                  backgroundImage: `url(${Desert})`,
                  backgroundRepeat: "no-repeat",
                  backgroundOrigin: "content-box",
                  backgroundPosition: "center",
                  backgroundSize: 500,
                }}
                contentArrowStyle={{}}
                date="20.12.2005"
                dateStyle = {{}}
                iconStyle={{
                  backgroundImage: `url(${mountain})`,
                  backgroundRepeat: "no-repeat",
                  backgroundOrigin: "content-box",
                  backgroundPosition: "center",
                  backgroundSize: 40,
                }}
              >
                <h3 className="vertical-timeline-element-title">
                  Web Designer
                </h3>
                <h4 className="vertical-timeline-element-subtitle">
                  San Francisco, CA
                </h4>
                <p>User Experience, Visual Design</p>
              </VerticalTimelineElement>
            </VerticalTimeline>
          </MDBContainer>
        </main>
        <FooterPage />
      </div>
    );
  }
}

export default main;
