import React, { Component } from "react";
import { MDBCol, MDBContainer, MDBRow,MDBCarousel, MDBCarouselInner, MDBCarouselItem, MDBTestimonial, MDBAvatar, MDBIcon } from "mdbreact";
import  {db} from "./index";
import CarouselFooter from "./components/CarouselFooter"

class footer extends Component {

  getfeedBack(){

  }

  getFeedBack(){
    db.collection("FeedBack").get()
    .then((querySnapshot) => {
      var MDBCarouselItems = [];
      var colums  = [];
      var fourColums = []; 
      MDBCarouselItems =  querySnapshot.map((item,index) => {
        return <MDBCol style={{display : "inline-block"}} md="3">
                            <MDBTestimonial>
                             <MDBAvatar  style={{width : "80px"}} className="mx-auto">
                               <img
                                 src="https://mdbootstrap.com/img/Photos/Avatars/img%20(26).jpg"
                                 alt=""
                                 className="rounded-circle img-fluid"
                               />
                           </MDBAvatar>
                             <h6 className="font-weight-bold mt-4"> ${item.data().name}</h6>
                            <p className="font-weight-normal">
                              <MDBIcon icon="quote-left" className="pr-2" />
                              ${item.data().feedBackTxt}
                             </p>
                          </MDBTestimonial>
                          </MDBCol>;
               }).map((item,index) => {
                if(index !== 3){
                  fourColums.push(item);
                }
                else if(fourColums.length){
                  var Id = index / 4;
                  if(Id == 0){
                    Id = 1;
                  }
                 return ( 
                          <MDBCarouselItem itemId = {Id} > 
                           {fourColums.pop()};
                           {fourColums.pop()};
                           {fourColums.pop()};
                           {fourColums.pop()};
                          </MDBCarouselItem>);
                }        
              });
              console.log(MDBCarouselItems); 
              this.state.CarouselItems = MDBCarouselItems;
            })}

componentDidMount() {
this.getFeedBack()
}


render() {
  return (
      <CarouselFooter itemArray={this.state.CarouselItems}/>
  )
}
}

export default footer;
