import React, { Component } from "react";
import { MDBCol, MDBContainer, MDBRow,MDBCarousel, MDBCarouselInner, MDBCarouselItem, MDBTestimonial, MDBAvatar, MDBIcon } from "mdbreact";
import  {db} from "./index";
import CarouselFooter from "./components/CarouselFooter"

class footer extends Component {
  state = {
    feedBack : [],
  }

  getFeedBack(){
    db.collection("FeedBack").get()
    .then((querySnapshot) => {
      this.state.feedBack   = querySnapshot.map((item,index)=>item);
    })
  }


componentDidMount() {
this.getFeedBack()
}


render() {
  return (
      <CarouselFooter feedBack={this.state.feedBack}/>
  )
}
}

export default footer;
