import React, { Component } from "react";
import {
  MDBContainer,
  MDBBtn,
  MDBModal,
  MDBModalBody,
  MDBModalHeader,
  MDBModalFooter,
} from "mdbreact";
import "../css/UploadEvent.css";
import "../css/Admin.css";
import InputDropzoneComponent from "./Components/InputDropzoneComponent";
import Resizer from "react-image-file-resizer";
class UploadFeedBack extends Component {
  state = {
    modal16: false,
    pictureName: "",
    pictureUrl: "",
    pictureFiles : [],
    isActive1: false,
    isActive2: false,
  };

  onRemoveFile = (file) =>{
    var tempPictures  = this.state.pictureFiles.filter((item,index) => {
        return item != file
    })
    this.setState({
        pictureFiles: tempPictures,
    })
  }
  onAddFile = (file) =>{
    this.setState({
        pictureFiles:[...this.state.pictureFiles,file],
    })
  }
  addPictureHandle = () =>{
    const storageRef = storage.ref();
    let imagesRef;
    let thumbnailsRef;
    this.state.pictureFiles.forEach((item,index)=>{
         imagesRef = storageRef.child(`CaruselPhotos/${item.name}`);
         thumbnailsRef = storageRef.child(
          `CaruselPhotos/thumbnails/${item.name}`
        );
        imagesRef.put(item).then((snapshot) => {
            imagesRef.getDownloadURL().then((url) => {
              this.setState({
                Pictures: [
                  ...this.state.Pictures,
                  { imageUrl: url, imageName: picture.name },
                ],
              });
              console.log("Uploaded file to CaruselPhotos");
              Resizer.imageFileResizer(
                picture,
                350,
                250,
                "JPEG",
                55,
                0,
                (uri) => {
                  thumbnailsRef.put(uri).then((snapshot) => {
                    console.log("Uploaded file to thumbnails");
                    this.setState({
                      ["modal17"]: !this.state["modal17"],
                      loading: false,
                    });

                  });
                },
                "blob"
              );
            });
          });
    })
  }

  clickHandler = async (event) => {
    event.preventDefault();
    let modalNumber = "modal" + 16;
    this.addPictureHandle();
   this.setState({
     ...this.state,
     [modalNumber]: !this.state[modalNumber],
   });
  };

  toggle = (nr) => () => {
    let modalNumber = "modal" + nr;
    this.setState({
      [modalNumber]: !this.state[modalNumber],
    });
  };


  render() {
    const { modalIsOpen } = this.props;
    return (
      <MDBContainer>
        <MDBBtn onClick={this.toggle(16)} rounded outline color="success">
          הוסף תמונה לגלרייה
        </MDBBtn>
        <MDBModal
          position="left-top"
          isOpen={this.state.modal16}
          toggle={this.toggle(16)}
        >
          <MDBModalHeader toggle={this.toggle(16)} dir="rtl">
            הוסף תמונה
          </MDBModalHeader>
          <form
            onSubmit={this.clickHandler}
          >
            <MDBModalBody style={{ maxHeight: "75vh" }}>
              <InputDropzoneComponent  onAddFile = {this.onAddFile }onRemoveFile = {this.onRemoveFile}/>
            </MDBModalBody>
            <MDBModalFooter>
              <MDBBtn color="secondary" onClick={this.toggle(16)}>
                ביטול
              </MDBBtn>
              <MDBBtn type="submit" color="primary">
                הוסף תמונה
              </MDBBtn>
            </MDBModalFooter>
          </form>
        </MDBModal>
      </MDBContainer>
    );
  }
}

export default UploadFeedBack;
