import React, { Component } from "react";
import {
  MDBContainer,
  MDBBtn,
  MDBModal,
  MDBModalBody,
  MDBModalHeader,
  MDBModalFooter,
  MDBInput,
  MDBDatePickerV5,
} from "mdbreact";
import UploadEventStyles from '../css/UploadEvent.module.css'
import '../css/UploadEvent.css'
import { storage, db } from '../index'


class UploadEvent extends Component {
  state = {
    modal16: false,
    eventName: "",
    filePath: "",
    date: "",
    audiance: "",
    shortDescription: "",
    fullDescription: "",
  };

  toggle = (nr) => () => {
    let modalNumber = "modal" + nr;
    this.setState({
      [modalNumber]: !this.state[modalNumber],
    });
  };

  eventClickedHandler = () => {
    var imageUrl
    console.log('eventHandler')
    let storageRef = storage.ref()
    let eventImagesRef = storageRef.child(`eventsImages/${this.state.filePath.name}`)
    eventImagesRef.put(this.state.filePath)
      .then(snapshot => {
        snapshot.ref.getDownloadURL()
          .then(url => {
            console.log('in url')
            db.collection('events').doc(`${this.state.eventName}`).set({
              eventName: this.state.eventName,
              url: url,
              audiance: this.state.audiance,
              shortDescription: this.state.shortDescription,
              fullDescription: this.state.fullDescription,
              date: this.state.date,
            })
            .then(() => {
              console.log('event added to firebase')
              
            }).catch(err => {
              console.log('err add to firestore',err)
              alert('event was not added to firebase please try again')
              snapshot.ref.delete()
            })
          }).catch(err => {
            console.log('err in download url',err)
            alert('an error occured please try again')
            snapshot.ref.delete()
          })
      }).catch(err => {
        console.log('err to upload image',err)
        alert('an error occured please try again')
      })
  }
  render() {
    return (
      <MDBContainer>
        <MDBBtn onClick={this.toggle(16)}>MDBModal</MDBBtn>
        <MDBModal
          position="left-top"
          isOpen={this.state.modal16}
          toggle={this.toggle(16)}
        >
          <MDBModalHeader toggle={this.toggle(16)} dir="rtl">
            הוסף מסע
          </MDBModalHeader>
          <MDBModalBody>
            <div className="form-group">
              <MDBInput
                type="text"
                labelClass={`col text-right `}
                className="col text-right"
                label="שם האירוע"
                getValue={(e) =>
                  this.setState({ ...this.state, eventName: e })
                }
              />
              <div className="input-group">
                <div className="input-group-prepend"></div>
                <div className="custom-file">
                  <input
                    type="file"
                    className="custom-file-input"
                    id="inputGroupFile01"
                    aria-describedby="inputGroupFileAddon01"
                    onChange={(event) =>
                      this.setState({ filePath: event.target.files[0] })
                    }
                  />
                  <label
                    className="custom-file-label"
                    htmlFor="inputGroupFile01"
                  >
                    {this.state.filePath.name}
                  </label>
                </div>
              </div>
              {/* <MDBDatePickerV5
                label="בחר תאריך"
                format="DD-MM-YYYY"
                todayLabel="It's today"
                getValue={(e) => this.setState({ ...this.state, date: e.getFullYear() })} /> */}
                <MDBInput type="date" getValue={(e) => this.setState({...this.state, date: e}) }/>
              {/* <input className="float-right mt-2"
              type="date"
              onChange={e => this.setState({...this.state, date: e.target.value})}/> */}
              <MDBInput
                type="text"
                labelClass="col
              text-right"
                label="קהל יעד"
                className="col text-right"
                getValue={(e) => this.setState({ ...this.state, audiance: e })
                }
              />
              <MDBInput
                type="textarea"
                labelClass="col text-right"
                label="תיאור קצר"
                className="col text-right maxHeight"
                getValue={(e) =>
                  this.setState({ ...this.state, shortDescription: e })
                }
              />
              <MDBInput
                type="textarea"
                labelClass="col text-right"
                label="תיאור מלא"
                className="col text-right maxHeight"
                getValue={(e) =>
                  this.setState({ ...this.state, fullDescription: e })
                }
              />
            </div>
          </MDBModalBody>
          <MDBModalFooter>
            <MDBBtn color="secondary" onClick={this.toggle(16)}>
              ביטול
            </MDBBtn>
            <MDBBtn color="primary" onClick={this.toggle(16)}>הוסף אירוע</MDBBtn>
          </MDBModalFooter>
        </MDBModal>
      </MDBContainer>
    );
  }
}

export default UploadEvent;
