import React, { Component } from "react";
import {
  MDBContainer,
  MDBBtn,
  MDBModal,
  MDBModalBody,
  MDBModalHeader,
  MDBModalFooter,
} from "mdbreact";
import "../css/UploadEvent.css";
import { storage } from "../index";
import "../css/Admin.css";
import InputDropzoneComponent from "./Components/InputDropzoneComponent";
import Resizer from "react-image-file-resizer";
class UploadFeedBack extends Component {
  state = {
    modal16: false,
    pictureName: "",
    pictureUrl: "",
    pictureFiles : [],
    isActive1: false,
    isActive2: false,
  };

  onRemoveFile = (file) =>{
    var tempPictures  = this.state.pictureFiles.filter((item,index) => {
        return item != file
    })
    this.setState({
        pictureFiles: tempPictures,
    })
  }
  onAddFile = (file) =>{
    this.setState({
        pictureFiles:[...this.state.pictureFiles,file],
    })
      console.log(this.state.pictureFiles);
  }


  clickHandler = async (event) => {
    event.preventDefault();
    let modalNumber = "modal" + 16;
    this.addPictureHandle();
     this.setState({
     ...this.state,
     [modalNumber]: !this.state[modalNumber],
   });
  };

  toggle = (nr) => () => {
    let modalNumber = "modal" + nr;
    this.setState({
      [modalNumber]: !this.state[modalNumber],
    });
  };


  render() {
    const { modalIsOpen } = this.props;
    return (
      <MDBContainer>
        <MDBBtn onClick={this.toggle(16)} rounded outline color="success">
          הוסף תמונה לגלרייה
        </MDBBtn>
        <MDBModal
          position="left-top"
          isOpen={this.state.modal16}
          toggle={this.toggle(16)}
        >
          <MDBModalHeader toggle={this.toggle(16)} dir="rtl">
            הוסף תמונה
          </MDBModalHeader>
          <form
            onSubmit={this.clickHandler}
          >
            <MDBModalBody style={{ maxHeight: "75vh" }}>
              <InputDropzoneComponent  onAddFile = {this.onAddFile } onRemoveFile = {this.onRemoveFile}/>
            </MDBModalBody>
            <MDBModalFooter>
              <MDBBtn color="secondary" onClick={this.toggle(16)}>
                ביטול
              </MDBBtn>
              <MDBBtn  onClick = {this.props. addPictureHandle = () =>{
    const storageRef = storage.ref();
     const resize = async (item,thumbnailsRef) => {
        Resizer.imageFileResizer(
            item,
            350,
            250,
            "JPEG",
            55,
            0,
            async (uri) => {
             await thumbnailsRef.put(uri).then((snapshot) => {
                alert("Uploaded file to thumbnails");
              });
            },
            "blob"
          );
    }
    this.state.pictureFiles.forEach( async (item,index)=>{
        var imagesRef = storageRef.child(`CaruselPhotos/${item.name}`);
        var  thumbnailsRef = storageRef.child(
          `CaruselPhotos/thumbnails/${item.name}`
        );
       await imagesRef.put(item).then(async (snapshot) => {
          alert("Uploaded file to carusel");
           await resize(item,thumbnailsRef);
            });
          });
  }} type="submit" color="primary">
                הוסף תמונות
              </MDBBtn>
            </MDBModalFooter>
          </form>
        </MDBModal>
      </MDBContainer>
    );
  }
}

export default UploadFeedBack;
